INSERT INTO `ls_advertisement` VALUES(53, 1, 655, 1333843200, 1333843200, NULL, '分词算法', '<p><span style="color:#000000;font-family:helvetica, tahoma, arial, sans-serif;font-size:14px;background-color:#ffffff">这里使用是zf默认的分词算法Zend_Search_Lucene_Analysis_Analyzer_Common_Text。另外你可以加上一个过滤方法。比如说过滤一些单词，比如“is”,&quot;a&nbsp;&quot;之类的。</span><br style="color:#000000; font-family:helvetica, tahoma, arial, sans-serif; font-size:14px; font-style:normal; font-variant:normal; font-weight:normal; letter-spacing:normal; line-height:25px; orphans:2; text-align:left; text-indent:0px; text-transform:none; white-space:normal; widows:2; word-spacing:0px; -webkit-text-size-adjust:auto; -webkit-text-stroke-width:0px; background-color:#ffffff; " /><br style="color:#000000; font-family:helvetica, tahoma, arial, sans-serif; font-size:14px; font-style:normal; font-variant:normal; font-weight:normal; letter-spacing:normal; line-height:25px; orphans:2; text-align:left; text-indent:0px; text-transform:none; white-space:normal; widows:2; word-spacing:0px; -webkit-text-size-adjust:auto; -webkit-text-stroke-width:0px; background-color:#ffffff; " /><span style="color:#000000;font-family:helvetica, tahoma, arial, sans-serif;font-size:14px;background-color:#ffffff">&nbsp;</span><img src="/public/advertisement/2012/04/08/b1e24857a1ea24181d2bc0ca096a686f1333906305.jpg" title="20090904_2053f81f6693936eaa87dd857vGLeFFH.jpg" border="0" hspace="0" vspace="0" style="float:none; " /></p><p><span style="font-family:helvetica, tahoma, arial, sans-serif; font-size:14px; background-color:#FFFFFF; ">&nbsp;第二步、自</span><span class="t_tag" style="font-family:helvetica, tahoma, arial, sans-serif; font-size:14px; background-color:#FFFFFF; ">定义</span><span style="font-family:helvetica, tahoma, arial, sans-serif; font-size:14px; background-color:#FFFFFF; ">自己的分词算法，可以参考手册，或者自己看Zend_Search_Lucene_Analysis_Analyzer_Common_Text类的实现。</span></p><p><span style="color:#000000;font-family:helvetica, tahoma, arial, sans-serif;font-size:14px;background-color:#ffffff">&nbsp;&nbsp;&nbsp;其中要注意的是过滤这点。由于我们的分词是二元分词，如果要过滤一些比如“的”、“啊”之类的单词，是无法使用内置的Tokens&nbsp;Filtering。我们需要是分词前先过滤调。这个可以在reset()里面实现</span><br style="color:#000000; font-family:helvetica, tahoma, arial, sans-serif; font-size:14px; font-style:normal; font-variant:normal; font-weight:normal; letter-spacing:normal; line-height:25px; orphans:2; text-align:left; text-indent:0px; text-transform:none; white-space:normal; widows:2; word-spacing:0px; -webkit-text-size-adjust:auto; -webkit-text-stroke-width:0px; background-color:#ffffff; " /><span style="color:#000000;font-family:helvetica, tahoma, arial, sans-serif;font-size:14px;background-color:#ffffff">&nbsp;&nbsp;&nbsp;例子。</span></p><pre class="brush:php;toolbar:false;">&lt;?\n          \nrequire_once ''Zend/Search/Lucene/Analysis/Analyzer.php'';\n          \nclass Phpbean_Lucene_Analyzer extends Zend_Search_Lucene_Analysis_Analyzer_Common {\n          \n              \n          \n    private $_position;\n          \n              \n          \n    private $_cnStopWords = array();\n          \n              \n          \n    public function setCnStopWords($cnStopWords){\n          \n        $this-&gt;_cnStopWords = $cnStopWords;\n          \n    }\n          \n          \n          \n    /**\n          \n     * Reset token stream\n          \n     */\n          \n    public function reset()\n          \n    {\n          \n        $this-&gt;_position = 0;\n          \n        $search = array(",", "/", "\\", ".", ";", ":", """, "!", "~", "`", "^", "(", ")", "?", "-", "t", "n", "''", "&lt;", "&gt;", "r", "rn", "$", "&amp;", "%", "#", "@", "+", "=", "{", "}", "[", "]", "：", "）", "（", "．", "。", "，", "！", "；", "“", "”", "‘", "’", "［", "］", "、", "—", "　", "《", "》", "－", "…", "【", "】",);\n          \n        $this-&gt;_input = str_replace($search,'' '',$this-&gt;_input);\n          \n        $this-&gt;_input = str_replace($this-&gt;_cnStopWords,'' '',$this-&gt;_input);\n          \n    }\n          \n          \n          \n    /**\n          \n     * Tokenization stream API\n          \n     * Get next token\n          \n     * Returns null at the end of stream\n          \n     *\n          \n     * @return Zend_Search_Lucene_Analysis_Token|null\n          \n     */\n          \n    public function nextToken()\n          \n    {\n          \n        if ($this-&gt;_input === null) {\n          \n            return null;\n          \n        }\n          \n        while ($this-&gt;_position &lt; strlen($this-&gt;_input)) {\n          \n            while ($this-&gt;_position &lt; strlen($this-&gt;_input) &amp;&amp;\n          \n                    $this-&gt;_input[$this-&gt;_position]=='' '' ) {\n          \n                $this-&gt;_position++;\n          \n            }\n          \n            $termStartPosition = $this-&gt;_position;      \n          \n            $temp_char = $this-&gt;_input[$this-&gt;_position];\n          \n            $isCnWord = false;\n          \n            if(ord($temp_char)&gt;127){  \n          \n                $i = 0;       \n          \n                while ($this-&gt;_position &lt; strlen($this-&gt;_input) &amp;&amp;\n          \n                ord( $this-&gt;_input[$this-&gt;_position] )&gt;127) {\n          \n                    $this-&gt;_position = $this-&gt;_position + 3;\n          \n                    $i ++;\n          \n                    if($i==2){\n          \n                        $isCnWord = true;\n          \n                        break;\n          \n                    }\n          \n                }\n          \n                if($i==1)continue;\n          \n            }else{\n          \n                while ($this-&gt;_position &lt; strlen($this-&gt;_input) &amp;&amp;\n          \n                ctype_alnum( $this-&gt;_input[$this-&gt;_position] )) {\n          \n                    $this-&gt;_position++;\n          \n                }\n          \n            }\n          \n            if ($this-&gt;_position == $termStartPosition) {\n          \n                return null;\n          \n            }\n          \n          \n          \n            $token = new Zend_Search_Lucene_Analysis_Token(\n          \n                                      substr($this-&gt;_input,\n          \n                                             $termStartPosition,\n          \n                                             $this-&gt;_position - $termStartPosition),\n          \n                                      $termStartPosition,\n          \n                                      $this-&gt;_position);\n          \n            $token = $this-&gt;normalize($token);\n          \n            if($isCnWord)$this-&gt;_position = $this-&gt;_position - 3;\n          \n            if ($token !== null) {\n          \n                return $token;\n          \n            }\n          \n        }\n          \n        return null;\n          \n    }\n          \n              \n          \n}\n          \n?&gt;</pre><p><br /></p><p><br /></p>', 1, '["\\/public\\/advertisement\\/2012\\/04\\/08\\/b1e24857a1ea24181d2bc0ca096a686f1333906305.jpg"]', '单词,算法,定义,方法,手册,例子,需要,', '', '14780624963', NULL, 1333882532, 1333882532);
