INSERT INTO `uu_article` (`arc_id`, `arc_cid`, `arc_title`, `arc_iscomment`, `arc_isrecommend`, `arc_ctype`, `arc_author`, `arc_hits`, `arc_created`, `arc_update`, `arc_tags`, `arc_state`, `arc_content`) VALUES
(181, '192,196', 'Yii扩展中通过widget方式使用Highcharts', 1, 1, 1, 5, 58, 1342083360, 1343747826, ' Highcharts', 3, '<p style="border:0px; font-size:13px; margin:0px 0px 9px; outline:0px; padding:0px; color:#4d4d4d; font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif; line-height:20px; text-align:left; white-space:normal; background-color:#ffffff; ">图表生成，在网上也找到了很多，给我感觉很好的是<a title="fusion" href="http://www.fusioncharts.com/" target="_blank" style="border:0px; margin:0px; outline:0px; padding:0px; text-decoration:none; color:#004d99; ">Fusion</a>不过要使用必须花些时间研究，在这里我介绍个可以通过<a title="Yii" href="http://www.yiiframework.com/" target="_blank" style="border:0px; margin:0px; outline:0px; padding:0px; text-decoration:none; color:#004d99; ">yii</a>使用的<a title="highcharts" href="http://www.highcharts.com/" target="_self" style="border:0px; margin:0px; outline:0px; padding:0px; text-decoration:none; color:#004d99; ">highchart</a>，通过yii的widget就可以方便的快捷的使用，基本满足使用需求。</p><p style="border:0px; font-size:13px; margin:0px 0px 9px; outline:0px; padding:0px; color:#4d4d4d; font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif; line-height:20px; text-align:left; white-space:normal; background-color:#ffffff; "><a title="Yii highchart" href="http://www.yiiframework.com/extension/highcharts" target="_blank" style="border:0px; margin:0px; outline:0px; padding:0px; text-decoration:none; color:#004d99; ">Yii&nbsp;highchart下载</a></p><p style="border:0px; font-size:13px; margin:0px 0px 9px; outline:0px; padding:0px; color:#4d4d4d; font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif; line-height:20px; text-align:left; white-space:normal; background-color:#ffffff; ">由于yii&nbsp;highchart&nbsp;下载&nbsp;中技术说明不是很多，下面给大家补充下其他属性的使用（通过数组实现）：</p><pre class="brush:php;toolbar:false;">$this -&gt;Widget(''ext.highcharts.HighchartsWidget'', array(\r\n       ''options'' =&gt; array(\r\n           ''title'' =&gt; array(''text'' =&gt; ''南京巾帼志愿者人数统计''),\r\n           ''xAxis'' =&gt; array(\r\n               ''categories'' =&gt; array(\r\n                   ''南京'', ''晓庄'', ''方山'',\r\n               ),\r\n               ''labels'' =&gt; array(\r\n                   ''rotation'' =&gt; -45,\r\n                   ''align'' =&gt; ''right'',\r\n                   ''style'' =&gt; array(\r\n                       ''font'' =&gt; ''normal 13px Verdana, sans-serif'',\r\n               )),\r\n           ),\r\n           ''yAxis'' =&gt; array(\r\n               ''min'' =&gt; 0,\r\n               ''title'' =&gt; array(''text'' =&gt; ''人数'')\r\n           ),\r\n           ''legend'' =&gt; array(\r\n               ''enabled'' =&gt; false,\r\n           ),\r\n           ''tooltip'' =&gt; array(\r\n               ''formatter'' =&gt; ''js:function() {\r\n           return \\''&lt;strong&gt;\\''+ this.x +\\''&lt;/strong&gt;\\''+\r\n               \\'': \\''+this.y +\r\n               \\''\\'';\r\n \r\n     }'' ),\r\n           ''series'' =&gt; array(\r\n               array(\r\n                   ''type'' =&gt; ''column'',\r\n                   ''data'' =&gt; array(1, 5, 4),\r\n                   ''dataLabels'' =&gt; array(\r\n                       ''enabled'' =&gt; true,\r\n                       ''rotation'' =&gt; -90,\r\n                       ''color'' =&gt; ''#FFFFFF'',\r\n                       ''align'' =&gt; ''center'',\r\n                       ''x'' =&gt; -3,\r\n                       ''y'' =&gt; 10,\r\n                       ''formatter'' =&gt; ''js:function() {\r\n                        return this.y;\r\n                        }'',\r\n                       ''style'' =&gt; array(\r\n                           ''font'' =&gt; ''normal 13px Verdana, sans-serif'',\r\n                       ),\r\n                   ),\r\n               ),\r\n           )\r\n       )\r\n   ));</pre><p><img src="http://www.wangyinneng.com/wp-content/uploads/2011/09/chart2.png" width="800" height="400" style="float:none;" border="0" hspace="0" vspace="0" /><br /></p><p><span style="color:#4d4d4d;font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif;font-size:13px;background-color:#ffffff">除了使用上面的数组外，我们还能使用json格式显示图表。</span><br style="color:#4d4d4d; font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif; font-size:13px; line-height:20px; text-align:left; white-space:normal; background-color:#ffffff; " /><span style="color:#4d4d4d;font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif;font-size:13px;background-color:#ffffff">如果更好，方便的图表类，欢迎交流。</span><br /></p>'),
(182, '192,196', 'YII中 增加 PHPMAILER扩展发邮件 php 邮件', 1, 1, 1, 5, 58, 1342083553, 1343748260, ' PHPMAILER', 3, '<p style="border:0px; font-size:13px; margin:0px 0px 9px; outline:0px; padding:0px; color:#4d4d4d; font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif; line-height:20px; text-align:left; white-space:normal; background-color:#ffffff; ">首先去官网下载&nbsp;mailer&nbsp;扩展</p><p style="border:0px; font-size:13px; margin:0px 0px 9px; outline:0px; padding:0px; color:#4d4d4d; font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif; line-height:20px; text-align:left; white-space:normal; background-color:#ffffff; ">在main.php&nbsp;中配置：</p><pre class="brush:php;toolbar:false;">‘mailer’ =&gt; array(\r\n‘class’ =&gt; ‘application.extensions.mailer.EMailer’,\r\n‘pathViews’ =&gt; ‘application.views.email’,\r\n‘pathLayouts’ =&gt; ‘application.views.email.layouts’\r\n),</pre><p><span style="color:#4d4d4d;font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif;font-size:13px;background-color:#ffffff">然后呢。在某一个controller.php中增加以下函数：</span><br style="color:#4d4d4d; font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif; font-size:13px; line-height:20px; text-align:left; white-space:normal; background-color:#ffffff; " /><span style="color:#4d4d4d;font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif;font-size:13px;background-color:#ffffff">我是使用的&nbsp;smtp&nbsp;动作，实验成功。发邮件:</span><br /></p><p></p><pre class="brush:php;toolbar:false;">$mail =Yii::app()-&gt;mailer;\r\n$message = ‘Hello World!’;\r\n$mail-&gt;Host = ’smtp.yeah.net’;\r\n$mail-&gt;Port = 25;\r\n$mail-&gt;IsSMTP();\r\n$mail-&gt;SMTPAuth= true;\r\n$mail-&gt;Username = “******”;//你的用户名，或者完整邮箱地址\r\n$mail-&gt;Password = “****”;//邮箱密码\r\n$mail-&gt;SetFrom(****@yeah.net’, ‘000000000′);//发送的邮箱和发送人\r\n$mail-&gt;AddAddress($this-&gt;email);\r\n$mail-&gt;Subject = ‘YII注册信息’;\r\n$mail-&gt;Body = $message;\r\n \r\nif ($mail-&gt;Send()) {\r\nreturn true;\r\n}\r\nelse\r\nreturn false;</pre><p></p><p style="border:0px; font-size:13px; margin:0px 0px 9px; outline:0px; padding:0px; color:#4d4d4d; font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif; line-height:20px; text-align:left; white-space:normal; background-color:#ffffff; ">至此。可以发邮件了。。</p><p style="border:0px; font-size:13px; margin:0px 0px 9px; outline:0px; padding:0px; color:#4d4d4d; font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif; line-height:20px; text-align:left; white-space:normal; background-color:#ffffff; ">如果你要用&nbsp;GMAIL发的话。。记得去PHP.INI中，把PHP的SSL模块加上，使用SSL去传输数据。就可以了。<br />原理是一样滴～～完</p><p></p><pre class="brush:php;toolbar:false;">require(“class.phpmailer.php”); //下载的文件必须放在该文件所在目录\r\n$mail = new PHPMailer(); //建立邮件发送类\r\n$address = $_POST[''address''];\r\n$mail-&gt;IsSMTP(); // 使用SMTP方式发送\r\n$mail-&gt;Host = “mail.xxxxx.com”; // 您的企业邮局域名\r\n$mail-&gt;SMTPAuth = true; // 启用SMTP验证功能\r\n$mail-&gt;Username = “user@xxxx.com”; // 邮局用户名(请填写完整的email地址)\r\n$mail-&gt;Password = “******”; // 邮局密码\r\n$mail-&gt;From = “user@xxxx.com”; //邮件发送者email地址\r\n$mail-&gt;FromName = “您的名称”;\r\n$mail-&gt;AddAddress(”$address”, “”);//收件人地址，可以替换成任何想要接收邮件的email信箱,格式是AddAddress(”收件人email”,”收件人姓名”)\r\n//$mail-&gt;AddReplyTo(””, “”);\r\n//$mail-&gt;AddAttachment(”/var/tmp/file.tar.gz”); // 添加附件\r\n//$mail-&gt;IsHTML(true); // set email format to HTML //是否使用HTML格式\r\n$mail-&gt;Subject = “PHPMailer测试邮件”; //邮件标题\r\n$mail-&gt;Body = “Hello,这是测试邮件”; //邮件内容\r\n$mail-&gt;AltBody = “This is the body in plain text for non-HTML mail clients”; //附加信息，可以省略\r\nif(!$mail-&gt;Send())\r\n{\r\necho “邮件发送失败.\r\n \r\n“;\r\necho “错误原因: ” . $mail-&gt;ErrorInfo;\r\nexit;\r\n}\r\necho “邮件发送成功”;</pre><p><span style="color:#4d4d4d;font-family:&#39;helvetica neue&#39;, arial, &#39;lucida grande&#39;, &#39;lucida sans unicode&#39;, &#39;microsoft yahei&#39;, sans-serif;font-size:13px;background-color:#ffffff"><br /></span></p>'),
(183, '192', '利用PHP生成随机生成中文段落，测试网站内容时专用', 1, 1, 1, 5, 43, 1342103030, 1343747967, '随机生成中文', 3, '<p>在创建网站的时候，很多时候都需要输入一些中文的段落来填充页面，大多数会是找一些新闻之类的来复制粘贴。</p><p>以下代码是利用php来随机生成一些中文段落，以便用来测试。</p><p><strong><span style="font-size:14px">GBK版：</span></strong></p><pre class="brush:php;toolbar:false;">&lt;?php\r\nheader(''Content-type:text/html;charset=gbk'');\r\n   \r\nfunction createWords($words = 128)\r\n{\r\n    $seperate = array("，","。","！","？","；");\r\n    $strings = '''';\r\n    for ($i=0; $i&lt;$words; $i++)\r\n    {\r\n        $strings .= chr(rand(0xB0,0xD6)).chr(rand(0xA1,0xFE));\r\n        if (fmod($i, 18) &gt; rand(10, 20))\r\n        {\r\n            $strings .= $seperate[rand(0, 4)];\r\n        }\r\n    }\r\n    return $strings;\r\n}\r\n   \r\n$paras = rand(1, 10);\r\n   \r\n$strings = '''';\r\n   \r\nfor($i=0; $i&lt;$paras; $i++)\r\n{\r\n    $strings .= ''&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;''.createWords(rand(100, 500)).''&lt;br /&gt;'';\r\n}\r\n?&gt;\r\n&lt;!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n    &lt;meta http-equiv="Content-Type" content="text/html; charset=gbk" /&gt;\r\n    &lt;title&gt;随机生成中文段落&lt;/title&gt;\r\n    &lt;style type="text/css"&gt;\r\n        body{\r\n            width: 960px;\r\n            margin: 0 auto;\r\n        }\r\n        h1 {\r\n            text-align: center;\r\n        }\r\n    &lt;/style&gt;\r\n&lt;/head&gt;\r\n   \r\n&lt;body&gt;\r\n    &lt;h1&gt;刷新页面可得到不同结果&lt;/h1&gt;\r\n    &lt;div&gt;&lt;?php echo $strings;?&gt;&lt;/div&gt;\r\n&lt;/body&gt;</pre><p><span style="font-size:14px">这个是测试地址（打开有点慢）：&nbsp;</span><a href="http://multi-birds.kilu.de/create-chinese.php">http://multi-birds.kilu.de/create-chinese.php</a></p><p><strong>UTF8版:</strong></p><pre class="brush:php;toolbar:false;">&lt;?php\r\nfunction createWords($words = 128)\r\n{\r\n    $seperate = array("，", "。", "！", "？", "；");\r\n    $strings = '''';\r\n    for ($i=0; $i&lt;128; $i++)\r\n    {\r\n        $strings .= iconv(''utf-16'', ''utf-8'', chr(rand(0x00, 0xFF)).chr(rand(0x4E, 0x99)));\r\n        if (fmod($i, 18) &gt; rand(10, 20))\r\n        {\r\n            $strings .= $seperate[rand(0, 4)];\r\n        }\r\n    }\r\n    return $strings;\r\n}\r\n  \r\n$paras = rand(1, 10);\r\n$strings = '''';\r\n  \r\nfor($i=0; $i&lt;$paras; $i++)\r\n{\r\n    $strings .= ''&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;''.createWords(rand(100, 500)).''&lt;br /&gt;'';\r\n}\r\n?&gt;\r\n&lt;!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"&gt;\r\n&lt;html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en"&gt;\r\n&lt;head&gt;\r\n    &lt;meta http-equiv="Content-Type" content="text/html; charset=utf-8" /&gt;\r\n    &lt;title&gt;测试&lt;/title&gt;\r\n    &lt;style type="text/css"&gt;\r\n        body{\r\n            width: 960px;\r\n            margin: 0 auto;\r\n        }\r\n        h1 {\r\n            text-align: center;\r\n        }\r\n    &lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n    &lt;h1&gt;刷新页面可得到不同结果&lt;/h1&gt;\r\n    &lt;div&gt;&lt;?php echo $strings;?&gt;&lt;/div&gt;\r\n&lt;/body&gt;\r\n&lt;/html&gt;</pre><p><span class="Apple-style-span" style="font-size:14px"><strong>这个是测试地址：&nbsp;</strong></span>&nbsp;<a href="http://multi4birds4test.sinaapp.com/create-chinese.php">http://multi4birds4test.sinaapp.com/create-chinese.php</a></p><p><br /></p>');
